import Link from 'next/link'
import {get} from "../services/rest_service";

export default function Post(props:any) {
  return (
      <div className="post-card">
        <img src={props.urlToImage} alt={props.title} />
          <div className="post-card__body">
            <h5 className="post-card__title">{props.title}</h5>
            <p className="post-card__text">{props.description}</p>
            <Link href={props.key || ''}><a className="btn btn-primary">Подробнее</a></Link>
          </div>
      </div>
  )
}

Post.getInitialProps = async (ctx:any) => {
  let post = []
  const res: any = await get(`${process.env.NEWS_API_URL}?q=${ctx.title}&from=2020-09-23&sortBy=publishedAt&apiKey=${process.env.NEWS_API_KEY}`);

  if (res.data && res.data.status === "ok") {
    post = res.data.articles;
  } else {
    console.error(res.error)
  }

  return {post}
};
